import React, { Component } from 'react'
import  { useState } from 'react'
import './Login.css'
import { Button, Form, FormGroup, Label, Input } 
    from 'reactstrap'
import axios from 'axios'

const [user, setUser ]  = useState('');
const [password , setPassword] = useState('');
function handleChange (name , value){
    if(name === 'usuario'){
        setUser(value)
    }
    else{        
        setPassword(value)        
    }
}

export default class Login extends Component {
    render() {
        return (
            <div className="completo">
            <Form className="login-form">
                <h1 className="font-w">Cripto App</h1>
                <h2 className="text-center">Bienvenido</h2>
                <FormGroup>
                    <Label className="usuario-l">Usuario</Label>
                    <Input className="usuario-i" type="email" name="Usuario" placeholder="Usuario" handleChange={handleChange}/>
                </FormGroup>
                <FormGroup>
                    <label>Contraseña</label>
                    <Input type="password" placeholder="Contraseña" name="Contraseña" handleChange={handleChange}/>
                </FormGroup>
                <Button className="btn-lg btn-dark btn-block">Entrar</Button>
            </Form>
            </div>
        )
    }
}
