import React, { useState } from 'react'
import Navigation from './Navigation'
import axios from 'axios'

import { Button }
    from 'reactstrap'

const Caesar = () => {

    const [mensaje, setMensaje] = useState('');
    const [k, setK] = useState(1);
    const [encripted, setEncripted] = useState(1);

    function handleChange(name, value) {
        if (name === 'mensaje') {
            setMensaje(value)
        }else{
            setK(value)
        }        
    }
    
    onsubmit= async e =>{
        console.log('mensaje: ',mensaje)
        console.log('mensaje: ',k)
        console.log('encripted: ',encripted)
        try{
            let config = {
                headers: {
                  'Access-Control-Allow-Origin': '*',
                }
              }
            let data ={
                'encript':encripted,
                'mensaje':mensaje,
                'k':k
            }
            let url='http://localhost:3000/caesar'
         setResp(await (await axios.post(url,data,config)).data)
         if (resp===''){
            console.log('resp=null')
        }
     }catch(e){
         console.log(e)
     }
     }


    return (
        <div>
            <Navigation />
            caesar
            <div class="input-group input-group-lg">
                <span class="input-group-text" id="inputGroup-sizing-lg">mensaje</span>
                <input type="text" class="form-control" name="mensaje" aria-label="Sizing example input" aria-describedby="inputGroup-sizing-lg" onChange={(e) => handleChange(e.target.name, e.target.value)} />
            </div>
            <label for="tentacles">Parametro k (1-26):</label>

            <input type="number"  name="tentacles"  onChange={(e) => handleChange(e.target.name, e.target.value)} 
                min="1" max="26" />

            <div className="form-check">
                <input className="form-check-input" type="radio"  id="flexRadioDefault1" name='en' checked={encripted===1} onChange={(e) => setEncripted(1)}/>
                <label className="form-check-label" for="flexRadioDefault1">
                    Cifrar
                </label>
            </div>
            <div className="form-check">
                <input className="form-check-input" type="radio"  id="flexRadioDefault2" name='de' checked={encripted===2 }onChange={(e) => setEncripted(2)}/>
                <label className="form-check-label" for="flexRadioDefault2">
                    Decifrar
                </label>
            </div>
            <Button onClick={onsubmit} className="btn-lg btn-dark btn-block">Enviar</Button>
        </div>
    )

}
export default Caesar
