import React, { useState, useRef } from 'react'
import ReCAPTCHA from "react-google-recaptcha";

import './Login.css'
import { Button, Form, FormGroup, Label }
    from 'reactstrap'
import Inputi from './Inputi'

import axios from 'axios'


const Login = () => {

    const [user, setUser] = useState('');
    const [password, setPassword] = useState('');
    const captcha=useRef(null)

    function handleChange (name , value){
        if(name === 'usuario'){
            setUser(value)
        }
        else{             
            setPassword(value)            
        }
    }
    const onChange = () =>{
        console.log(captcha.current.getValue())
    }
    
    onsubmit= async e =>{
        e.preventDefault();
        let resp
        
        const formData = new FormData()
        formData.append('email',user)
        formData.append('password',password)
        console.log('form: ',formData.get('email'))
        try{
            let config = {
                headers: {
                  'Access-Control-Allow-Origin': '*',
                }
              }
            let data ={
                'email':user,
                'password':password
            }
            let url='http://localhost:8080/users/login'
         resp=await axios.post(url,data,config)
         if (resp.data===''){
            console.log('resp=null')
        }
     }catch(e){
         console.log(e)
     }
        console.log('respuesta: ',resp.data)
        console.log('pass: ',password)
        
    } 
    
    return (
        <div className="completo">
            <Form className="login-form">
                <h1 className="font-w">Cripto App</h1>
                <h2 className="text-center">Bienvenido</h2>
                <FormGroup>
                    <Label className="usuario-l">Usuario</Label>
                    
                    <Inputi 
                    atribute={{
                        id:'usuario',
                        name:'usuario',
                        type:'text',
                        placeholder:'Ingrese su usuario'
                    }}
                    handleChange={handleChange}
                    className="usuario-i"
                />
                    
                </FormGroup>
                <hr size="15px"  className='usuario-i'/>
                <FormGroup>
                    <label>Contraseña</label>
                    <Inputi
                    atribute={{
                        id:'contraseña',
                        name:'contraseña',
                        type:'password',
                        placeholder:'Ingrese su contraseña'
                    }}
                    handleChange={handleChange}
                />
                </FormGroup>
                <div>
                <ReCAPTCHA ref={captcha}
                    sitekey="6LcmVmwbAAAAACTNG3rP4LmeD_Wc6bcPKVCopbOl"
                    onChange={onChange}
                />,
                </div>
                    <Button onClick={ onsubmit} className="btn-lg btn-dark btn-block">Entrar</Button>
                
            </Form>
        </div>
    )

}
export default Login
