import React, { useState } from 'react'

import './Login.css'
import { Button, Form, FormGroup, Label }
    from 'reactstrap'
import Inputi from './Inputi'

import axios from 'axios'


const Login = () => {

    const [user, setUser] = useState('');
    const [password, setPassword] = useState('');

    function handleChange (name , value){
        if(name === 'usuario'){
            setUser(value)
        }
        else{             
            setPassword(value)            
        }
    }
    
    onsubmit= async e =>{
        let resp=await axios.post('http://localhost:8080/users/login/',{
            email: user,
           name:'holo',
           password:password,
           puntaje:0
        })
        e.preventDefault();
        console.log('respuesta: ',resp)
    } 

    return (
        <div className="completo">
            <Form className="login-form">
                <h1 className="font-w">Cripto App</h1>
                <h2 className="text-center">Bienvenido</h2>
                <FormGroup>
                    <Label className="usuario-l">Usuario</Label>
                    
                    <Inputi 
                    atribute={{
                        id:'usuario',
                        name:'usuario',
                        type:'text',
                        placeholder:'Ingrese su usuario'
                    }}
                    handleChange={handleChange}
                    className="usuario-i"
                />
                    
                </FormGroup>
                <hr size="15px"  className='usuario-i'/>
                <FormGroup>
                    <label>Contraseña</label>
                    <Inputi
                    atribute={{
                        id:'contraseña',
                        name:'contraseña',
                        type:'password',
                        placeholder:'Ingrese su contraseña'
                    }}
                    handleChange={handleChange}
                />
                </FormGroup>
                
                    <Button onClick={ onsubmit} className="btn-lg btn-dark btn-block">Entrar</Button>
                
            </Form>
        </div>
    )

}
export default Login
